INSERT INTO `ram` (`id`, `ques`, `ans`) VALUES
(1, 'चौपाई :	उघरहिं अंत न होइ निबाहू। कालनेमि जिमि रावन राहू॥', 'अर्थ:यह चौपाई बालकाण्ड के आरम्भ की है। कार्य की सफलता मे संदेह है।\r\n'),
(2, 'चौपाई :गरल सुधा रिपु करहिं मिताई। गोपद सिंधु अनल सितला', 'अर्थ:यह चौपाई सुन्दरकाण्ड मे हनुमान जी के लंका मे प्रवेश करने के समय की है। प्रश्न बहुत श्रेष्ठ है कार्य सिद्ध होगा।\r\n'),
(3, 'चौपाई :	प्रबिसि नगर कीजे सब काजा। हृदय राखि कोसलपुर राजा॥\r\n', 'अर्थ:	यह चौपाई सुन्दरकाण्ड मे हनुमान जी के लंका मे प्रवेश करने के समय की है। अर्थ यह है कि भगवान के नाम का स्मरण करते हुये कार्य शुरू करें सफलता मिलेगी।\r\n'),
(4, 'चौपाई :	बिधि बस सुजन कुसंगत परहीं। फनि मनि सम निज गुन अनुसरहीं॥\r\n', 'अर्थ:	यह चौपाई बालकाण्ड के आरम्भ की है। अर्थ यह है कि बुरे लोगों का संग छोड़ दें कार्य की सफलता मे संदेह है।'),
(5, 'चौपाई :	मुद मंगलमय संत समाजू। जो जग जंगम तीरथ राजू॥\r\n', 'अर्थ:	यह चौपाई बालकाण्ड मे संत समाजरुपी तीर्थ वर्णन मे आती है। अर्थ यह है कि कार्य सिद्ध होगा।\r\n'),
(6, 'चौपाई :	बरुन कुबेर सुरेस समीरा। रन सन्मुखधरि काहु न धीरा॥\r\n', 'अर्थ:	यह चौपाई रावण वध पर मंदोदरी के विलाप के संदर्भ मे है। कार्य पूरा होने मे संदेह है।\r\n'),
(7, 'चौपाई :	सुनु सिय सत्य असीस हमारी। पूजहि मन कामना तुम्हारी॥\r\n', 'अर्थ:	यह चौपाई बालकाण्ड मे सीता जी को गौरी जी का आशिर्वाद है। प्रश्न उत्तम है कार्य सिद्ध होगा।\r\n'),
(8, 'चौपाई :	सुफल मनोरथ हो हुँ तुम्हारे। रामु लखनु सुनि भए सुखारे॥\r\n', 'अर्थ:	यह चौपाई विश्वामित्र का आशिर्वाद है। प्रश्न उत्तम है कार्य सिद्ध होगा।\r\n'),
(9, 'चौपाई : होइहि सोइ जो राम रचि राखा। को करितर्क बढ़ावै साखा॥\r\n', 'अर्थ: यह चौपाई बालकाण्ड शिव पार्वती संवाद मे है। कार्य पूरा होने मे संदेह है। प्रभु पर छोड़ दें।\r\n ');
INSERT INTO `tarot` (`id`, `card`, `img`, `des`, `yn`) VALUES
(1, 'ऐस ऑफ़ कप्स ( Ace of Cups )', '/tarot/images/Rider_Waite/normal/cups01.jpg ', 'प्यार, खुशी और फल की शुरुआत। आध्यात्मिकता भौतिक दुनिया को सहायता करती है।', 'प्यार, खुशी और फल की शुरुआत। आध्यात्मिकता भौतिक दुनिया को सहायता करती है।'),
(2, 'टू ऑफ कप ( Two of Cups ) ', '/tarot/images/Rider_Waite/normal/cups02.jpg', 'यह कार्ड बैलेंस दर्शाता है । आप विपरीत लिंग के साथ दोस्ती में प्रवेश कर सकते हैं।', 'यह कार्ड बैलेंस दर्शाता है । आप विपरीत लिंग के साथ दोस्ती में प्रवेश कर सकते हैं।'),
(3, 'थ्री ऑफ कप ( Three of Cups )', '/tarot/images/Rider_Waite/normal/cups03.jpg', 'उपलब्धि और बहुतायत आपके रास्ते का नेतृत्व कर रहे हैं ।', 'उपलब्धि और बहुतायत आपके रास्ते का नेतृत्व कर रहे हैं ।'),
(4, 'फोर ऑफ कप ( Four of Cups )', '/tarot/images/Rider_Waite/normal/cups04.jpg', '‘फोर ऑफ कप’ कभी कभी उदासीनता का एक संकेत देता है. आप वास्तव में किसी के बारे में बहुत परवाह नहीं है. आपको जीवन नीरस और साधारण लगता है. क्योंकि आप सफलता को पाने की रुचि खो देते हैं जो आपको ख़ुशी प्रदान करे. आप किसी भी दिशा में बहुत प्रयास करने की कोशिश नहीं करते और न ही इस बात से प्रेरित होते हैं. ऐसे समय में, ‘फोर ऑफ कप’ दिखा सकते हैं कि आप भावनात्मक रूप से ठहराव लिए हैं. आपको ध्यान केंद्रित करने की आवश्यकता है जिसके कारण आपका मार्ग स्पष्ट हो सके. अपने आप को अपने परिवेश में खोलें. जल्दी ही आप अपने रास्ते पर फिर से आ जाएंगे|', '‘फोर ऑफ कप’ कभी कभी उदासीनता का एक संकेत देता है. आप वास्तव में किसी के बारे में बहुत परवाह नहीं है. आपको जीवन नीरस और साधारण लगता है. क्योंकि आप सफलता को पाने की रुचि खो देते हैं जो आपको ख़ुशी प्रदान करे. आप किसी भी दिशा में बहुत प्रयास करने की कोशिश नहीं करते और न ही इस बात से प्रेरित होते हैं. ऐसे समय में, ‘फोर ऑफ कप’ दिखा सकते हैं कि आप भावनात्मक रूप से ठहराव लिए हैं. आपको ध्यान केंद्रित करने की आवश्यकता है जिसके कारण आपका मार्ग स्पष्ट हो सके. अपने आप को अपने परिवेश में खोलें. जल्दी ही आप अपने रास्ते पर फिर से आ जाएंगे|'),
(5, 'फाइव ऑफ कप ( Five of Cups )', '/tarot/images/Rider_Waite/normal/cups05.jpg', 'फाइव ऑफ कप ( Five of Cups ), इसका अर्थ है असंतोष और नुकसान। ', 'फाइव ऑफ कप ( Five of Cups ), इसका अर्थ है असंतोष और नुकसान। '),
(6, 'सिक्स ऑफ कप ( Six of Cups )', '/tarot/images/Rider_Waite/normal/cups06.jpg', 'अतीत से कुछ करने से आपको खुशी मिलेगी  | शायद बचपन के दोस्त से मिलेंगे।', 'अतीत से कुछ करने से आपको खुशी मिलेगी  | शायद बचपन के दोस्त से मिलेंगे।'),
(7, 'सेवन ऑफ कप ( Seven of Cups )', '/tarot/images/Rider_Waite/normal/cups07.jpg', '“सेवन ऑफ कप” ध्यान और प्रतिबद्धता की कमी के साथ भी संबद्ध किया जा सकता है. यह बारीकी से परीक्षण करने के लिए विचारों को डालने से बचने के लिए संबंधित हो सकता है. इस कार्ड के साथ एक व्यक्ति खुद विचारों में रहता है और जीवन में इस तरह के एक दृष्टिकोण की वजह से अनेक चीजों को खो देता है|', 'इस कार्ड द्वारा सपनों, आशाओं और महत्वाकांक्षाओं का वर्णन किया गया है। आपकी इच्छाएं  केंद्रित नहीं हैं।'),
(8, 'एट ऑफ कप ( Eight of Cups )', '/tarot/images/Rider_Waite/normal/cups08.jpg', ' ‘एट ऑफ कप’ के साथ इस बात का संकेत देता है कि उसने निराशाजनक स्थिति को छोड़ दिया है.वह आम तौर पर खुद को खोल देता है और अक्सर एक अज्ञात लंबी यात्रा आरंभ कर देता है. इस तरह का व्यक्ति जाने देने में विश्वास रखता है. जो उसने शुरु किया हो  वह उसे समाप्त करके फिर चलता है|', ' ‘एट ऑफ कप’ के साथ इस बात का संकेत देता है कि उसने निराशाजनक स्थिति को छोड़ दिया है.वह आम तौर पर खुद को खोल देता है और अक्सर एक अज्ञात लंबी यात्रा आरंभ कर देता है. इस तरह का व्यक्ति जाने देने में विश्वास रखता है. जो उसने शुरु किया हो  वह उसे समाप्त करके फिर चलता है|'),
(9, 'नाइन ऑफ कप ( Nine  of Cups )', '/tarot/images/Rider_Waite/normal/cups09.jpg', 'आपकी इच्छा सच होने वाली है. यह एक अद्भुत संभावना है, लेकिन आप सुनिश्चित करें कि आप जानते हैं कि आप वास्तव में क्या चाहते हैं और ज़िम्मेदारी  को अपनी इच्छा के साथ  स्वीकार किया जाना चाहिए. और अच्छे भाग्य का आनंद लें. , ‘नाइन ऑफ कप’ के साथ व्यक्ति अपनी इच्छाओं को पाता है. इस तरह के एक व्यक्ति को हमेशा अपने लक्ष्यों को प्राप्त कर लेते हैं. , ‘नाइन ऑफ कप’ के साथ व्यक्ति के सपने हमेशा सच होते हैं', '‘नाइन ऑफ कप’ के साथ व्यक्ति के सपने हमेशा सच होते हैं'),
(10, 'टेन ऑफ कप ( Ten of Cups )', '/tarot/images/Rider_Waite/normal/cups10.jpg', '‘टेन ऑफ कप’ को पारिवारिक सहयोग और परिवार के अन्य सदस्यों के साथ संबंध में अच्छे रिश्ते को दर्शाता है. इसके साथ यह एक दूसरे को माफ करने की बात भी कहता है गलतियों को क्षमा करने की बात कहता है', '‘टेन ऑफ कप’ को पारिवारिक सहयोग और परिवार के अन्य सदस्यों के साथ संबंध में अच्छे रिश्ते को दर्शाता है. इसके साथ यह एक दूसरे को माफ करने की बात भी कहता है गलतियों को क्षमा करने की बात कहता है |'),
(11, 'कप्स का पेज ( Page of Cups )', '/tarot/images/Rider_Waite/normal/cups11.jpg', 'पेज ऑफ कप’ आपको बताता है कि आपको किसी के प्रति प्रेम प्रदर्शित करने के लिए आकर्षक विचारशील इशारा करना चाहिए. यह आपसे पूछता है के लिए सहानुभूति और समझ व्यक्त. यह भी कहता है कि तुम अपने आप को किसी को चोट पहुँचाने के लिए माफ कर दो और कोई ओर जो तुम्हें चोट पहुंचाता है तो तुम उसे क्षमा कर दो. ‘पेज ऑफ कप’ आपसे पूछता है तुम बाहर तक पहुँचो और किसी के दिल को छू लो. तुम आगे जाओ और एक टूटे हुए रिश्ते में सुधार करने का प्रयास करो. आप भी किसी दिन को रोशन करने और क्रोध के बजाय देखभाल के साथ जवाब दो. ‘पेज ऑफ कप’ तुम्हे न्यायाधीश या निंदा करने से मना करता है |', 'आप भी किसी दिन को रोशन करने और क्रोध के बजाय देखभाल के साथ जवाब दो. ‘पेज ऑफ कप’ तुम्हे न्यायाधीश या निंदा करने से मना करता है |'),
(12, 'नाइट ऑफ़ कप्स ( Knight of Cups )', '/tarot/images/Rider_Waite/normal/cups12.jpg', 'यह कार्ड एक सुस्त लेकिन आकर्षक युवा के बारे में बताता है। भौतिक वस्तुओं से उसका सुख प्राप्त होता है।', 'वह आगे बढ़ते रहे | '),
(13, 'क्वीन ऑफ कप ( Queen of Cups )', '/tarot/images/Rider_Waite/normal/cups13.jpg', ' यह महिला जब चाहें तब सामान प्राप्त कर सकती है  | वह सीधा और प्रतिबद्ध है, हालांकि वह आपकी मदद करने के लिए अपने रास्ते से बाहर नहीं जाएगी।', ' यह महिला जब चाहें तब सामान प्राप्त कर सकती है  | वह सीधा और प्रतिबद्ध है, हालांकि वह आपकी मदद करने के लिए अपने रास्ते से बाहर नहीं जाएगी।'),
(14, 'द किंग ऑफ कप ( King of Cups )', '/tarot/images/Rider_Waite/normal/cups14.jpg', 'कप का राजा कोई दयालु है, कानून और व्यवसाय दोनों में कुशल है जो उसके व्यवहार के लिए बहुत जवाबदेह है। वह किसी धार्मिक या सांसारिक संगठन से जुड़ा हो सकता है।', 'कप का राजा कोई दयालु है, कानून और व्यवसाय दोनों में कुशल है जो उसके व्यवहार के लिए बहुत जवाबदेह है। वह किसी धार्मिक या सांसारिक संगठन से जुड़ा हो सकता है।'),
(15, 'ऐस ऑफ वैँड्स ( Ace of Wands )', '/tarot/images/Rider_Waite/normal/wands01.jpg', 'यह कार्ड एक कार्य के प्रारंभ के साथ संबद्ध होता है। इस कार्ड की प्रकृति सामान्य तौर पर आसपास के कार्ड से पता चलती है। यह निर्माण, आविष्कार, सत्ता, पैसा, भाग्य, वंशानुक्रम और कभी कभी विभिन्न प्रकार की आपदाओं के साथ संबद्ध होता है। आप निष्कर्ष के द्वारा कह सकते हैं कि यह कार्ड कभी कभी अपने चारों ओर मौजूद कार्ड पर निर्भर होता है। अगर ऐसा नहीं भी है, तब भी परिणाम निश्चित रूप से  होगा। इस कार्ड का तात्पर्य है कि व्यक्ति जो कुछ भी  कार्य कर रहा है उसमे उसे सर्वश्रेष्ठ से अधिक देना होगा। उसे  साहसी कदम उठाना पड़ेगा।', ''),
(16, 'टू ऑफ वैन्डस ( Two of Wands )', '/tarot/images/Rider_Waite/normal/wands02.jpg', 'ऐसे व्यक्ति किसी क्षेत्र में अग्रणी होते हैं और कुछ नयी रचना करते हैं। इस कार्ड के साथ व्यक्ति दूसरों को अपनी स्थिति पर ले आता है। ऐसे व्यक्ति को जो कुछ भी उनके मन में हो उससे संबधित बात करने के लिये तीव्र इच्छुक होते है।', ''),
(17, 'थ्री ऑफ वैंडस ( Three of Wands )', '/tarot/images/Rider_Waite/normal/wands03.jpg', 'द थ्री ऑफ वैंडस के साथ व्यक्ति एक जिम्मेदार पद को ग्रहण करते हैं और एक प्रतिनिधि के रूप में सेवारत रहते हैं। एक उदाहरण के तौर पर ऐसे व्यक्ति के पीछे रैलियों का समूह होता है और हमेशा आगे की योजना बनाते है। इस कार्ड के साथ व्यक्ति मुख्य भूमिका में एक नेता के रूप में कार्यरत होते है।', ''),
(18, 'फोर आफ वैन्ड्स ( Four of Wands )', '/tarot/images/Rider_Waite/normal/wands04.jpg', 'यह कार्ड निर्माण और स्थापना को दर्शाता है। यह कार्ड सामान्य तौर पर अप्रत्याशित शुभ भाग्य के साथ संबंधित है।', ''),
(19, 'फाइव आफ वैन्ड्स ( Five of Wands )', '/tarot/images/Rider_Waite/normal/wands05.jpg', 'द फाइवआआफ वांड्स असीमित दर्द को दर्शाता है। कार्य की गतिविधि इतनी बढ़ जाती है कि लोगों द्वारा इस पर ध्यान दिया जाता है और जरूरी नहीं कि दूसरों के द्वारा इसको सराहना मिले। अब आपको अपने कार्य के लिये और अधिक महात्वाकांक्षी होना होगा, जिससे कार्य में सफलता मिले।', ''),
(20, 'सिक्स आफ वैन्डस ( Six  of Wands )', '/tarot/images/Rider_Waite/normal/wands06.jpg', 'यह कार्ड आपके कार्यों के आकलन और विश्लेषण के साथ संबद्ध होता है। यह सफलता के क्रम में अधिक से अधिक ज्ञान और सुधार के साथ जुड़ा होता है।', ''),
(21, 'सेवन ऑफ वैन्ड्स ( Seven  of Wands )', '/tarot/images/Rider_Waite/normal/wands07.jpg', 'यह कार्ड अपने कार्यो में नई खोज का सुझाव देता है। यह कार्ड कार्य के नए क्षेत्रों में परिवर्तन करने की खोज के साथ संबद्ध किया जाता है। यह कार्ड प्रतिनिधित्व करता है कि आगे जाने की तुलना में यह कार्य का मूल उद्देश्य था.', ''),
(22, 'ऐट आफ वैंड्स ( Eight  of Wands )', '/tarot/images/Rider_Waite/normal/wands08.jpg', 'यह कार्ड संचारण, बिक्री, उन्नति, क्रय- विक्रय और कार्य को बनाने या विचार करने को दर्शाता है। यह विवाहित व्यक्ति के घरेलू विवादों का भी प्रतिनिधित्व करता है।', ''),
(23, 'नाइन आफ वैन्ड्स ( Nine  of Wands )', '/tarot/images/Rider_Waite/normal/wands09.jpg', 'यह कार्ड किसी भी कार्य के बारे में चिंतन शक्ति को दर्शाता है। यह व्यक्ति के मन से सभी संदेह को दूर कर देता है।', ''),
(24, 'टेन आफ वैन्डस ( Ten of Wands )', '/tarot/images/Rider_Waite/normal/wands10.jpg', 'बिना विचार किये या उत्तेजना के साथ आरंभ किया गया कार्य जो कि भीमकाय  बन जाय और नियंत्रण से बाहर हो जाए। अन्य लोग उस योजना का प्रतिरुपण या उपयोग करते हैं। आप या तो नियंत्रण में नहीं रख पाए या आपके अधिक नियंत्रण में हो और आप सब कुछ पुन: सब कुछ आरंभ करें।', ''),
(25, 'पेज आफ वैन्डस ( Page of Wands )', '/tarot/images/Rider_Waite/normal/wands11.jpg', 'द पेज आफ  वैन्ड्स दर्शाता है कि परिवार का नवयुवक , जो कि नवयुवती की तलाश में है।', ''),
(26, 'नाइट आफ वैन्डस ( Knight of Wands )', '/tarot/images/Rider_Waite/normal/wands12.jpg', 'यह कार्ड प्रस्थान, उड़ान, अनुपस्थिति, निवास परिवर्तन और अलगाव से संबद्ध होता है।', ''),
(27, 'क्यूइन आफ वैंडस ( Queen of Wands )', '/tarot/images/Rider_Waite/normal/wands13.jpg', 'यह कार्ड अच्छी फसल को दर्शाता है, जिसके अनेक मायने हो सकते हैं। यह कार्ड प्रेम या धन के साथ भी संबद्ध होता है।', ''),
(28, 'किंग आफ वैन्डस ( King of Wands )', '/tarot/images/Rider_Waite/normal/wands14.jpg', 'यह कार्ड असीम शक्ति को दर्शाता है। इसको अच्छे विवाह से भी संबद्ध किया जाता है। यह कार्ड ईमानदारी और अप्रत्याशित विरासत से भी संबंधित होता है।', ''),
(29, 'ऐस ऑफ़ स्वॉर्ड्स ( Ace of Swords )', '/tarot/images/Rider_Waite/normal/swords01.jpg', 'यह एक महान विजय का संकेत देता है।', ''),
(30, 'टू ऑफ स्वॉर्ड्स ( Two of Swords )', '/tarot/images/Rider_Waite/normal/swords02.jpg', 'यह कार्ड रिश्ते में तनाव या गतिरोध का द्योतक है। ', 'यह कार्ड रिश्ते में तनाव या गतिरोध का द्योतक है। '),
(31, 'थ्री ऑफ स्वॉर्ड्स ( Three of Swords )', '/tarot/images/Rider_Waite/normal/swords03.jpg', 'यह कार्ड व्यक्ति की ऐसी परिस्थिति को दर्शाता है कि वह पुराने और नए के बीच फैसला करने में असमर्थ होता है। वह इस दुविधा में रहता है कि वह जीवन में नई चीजों के साथ रहे या अतीत उससे अधिक बेहतर था।', ''),
(32, 'फॉर ऑफ स्वोसर्ड (Four of Swords)', '/tarot/images/Rider_Waite/normal/swords04.jpg', 'द फॉर ऑफ स्वार्ड  विश्राम की पुनराप्ति के स्वास्थ्य के समय की है, द र्थी ऑफ स्वार्ड में हानि या नुकसान के अनुभव को आत्मसात करता है। यह सावधानी, आश्रय, एकांत, निर्वासन से संबंधित है।', ''),
(33, 'फ़ाइव ऑफ स्वोर्ड ( Five of Swords )', '/tarot/images/Rider_Waite/normal/swords05.jpg', 'यह कार्ड व्यक्ति के भाग्य पर हमले के साथ संबद्ध किया जाता है। यह अपमान, विनाश, अपयश, निरादर, और हानि के साथ संबंधित है।', ''),
(34, 'सिक्स ऑफ स्वोर्डस ( Six of Swords )', '/tarot/images/Rider_Waite/normal/swords06.jpg', 'द सिक्स ऑफ स्वार्ड समय से बाहर निकलना, यात्रा, अध्ययन और सीखने  से संबंधित है। यह आपके ज्ञान और ज्ञान के संचय का सशक्त माध्यम है। यह आपको यात्रा करने के लिये और आंतरिक और बाहरी दुनिया की  खोज करने के लिये प्रेरित करता है।', ''),
(35, 'सेवन ऑफ स्वॉर्ड्स ( Seven of Swords )', '/tarot/images/Rider_Waite/normal/swords07.jpg', 'यह कार्ड व्यक्ति के मन में संदेह बनाता है कि उसके प्रयास  और विचार महत्वपूर्ण है कि नही। यह कार्ड सुरक्षित रहने की क्षमता के बाद अच्छे जीवन का वादा करता है।', ''),
(36, 'ऐट ऑफ स्वोर्डस ( Eight of Swords )', '/tarot/images/Rider_Waite/normal/swords08.jpg', 'ऐंट ऑफ स्वार्ड आपकी भावनात्मक और शारीरिक आवश्यकताओं के लिये निरंतर विचार और हानि के विश्लेषण को दर्शाता है। जैसी विचार और योजनायें होती है, उसी रुप में विचार और योजनायें मस्तिष्क में रहती है। आप अपने आप से अलग होकर सोचेंगे,  इसलिये आपका बाहरी अनुभव करने के लिए कोई संबंध नही होता है ।', ''),
(37, 'नाइन ऑफ स्वोर्डस ( Nine of Swords )', '/tarot/images/Rider_Waite/normal/swords09.jpg', 'नाइन ऑफ स्वार्ड जो कि  द ऐंट ऑफ स्वार्ड से जारी उसकी अलग सोच के परिणामस्वरूप निराशा, उदासी, और हताशा को दर्शाता है। हम सब दुख और पीड़ा के लिए अभिशप्त होते हैं। प्रत्येक चीज समाप्त और क्षीण हो जाती है।', ''),
(38, 'टेन ऑफ स्वोर्डस ( Ten of Swords )', '/tarot/images/Rider_Waite/normal/swords10.jpg', 'यह कार्ड सोच की प्रक्रिया के टूटने के साथ संबद्ध होता है। यह घनिष्टता से क्रोध और झुंझुलाहट के साथ संबंधित है। यह दूसरों द्वारा दी गई यातना और मानसिक दुख के साथ संबद्ध होता है।', ''),
(39, 'पेज ऑफ स्वोर्ड्स ( Page of Swords )', '/tarot/images/Rider_Waite/normal/swords11.jpg', 'पेज ऑफ स्वार्ड को  ईर्ष्यालु जवान स्त्री माना जाता है जो सिर्फ खामोशी से क्या हो रहा है का अवलोकन करती है। इस कार्ड को अधिकार, सतर्कता, जासूसी, निरीक्षण और परीक्षा के साथ संबद्ध किया जा सकता है।', ''),
(40, 'नाईट ऑफ स्वोर्ड्स ( Knight of Swords )', '/tarot/images/Rider_Waite/normal/swords12.jpg', 'इस कार्ड को आदर्शवादी और सकारात्मक विचारों से भरा माना जाता है। यह कौशल, बहादुरी, क्षमता, रक्षा, दुश्मनी, क्रोध, युद्ध, विनाश, विरोध, प्रतिरोध और पतन के साथ संबद्ध किया जाता है।', ''),
(41, 'क्वीन ऑफ स्वोर्ड्स ( Queen of Swords )', '/tarot/images/Rider_Waite/normal/swords13.jpg', 'यह कार्ड एक स्त्री का है जिसने प्यार को खो दिया है या वह विधवा है। इसका संबंध स्त्री के दुख से, शर्मिंदगी, अनुपस्थिति, बाँझपन, शोक और अलगाव से संबंधित है।', ''),
(42, 'द किंग ऑफ स्वार्ड ( King of Swords )', '/tarot/images/Rider_Waite/normal/swords14.jpg', 'द किंग ऑफ स्वार्ड गंभीर व्यवसायिक व्यक्ति है। यह एक कार्यालय में काम करता है और एक वकील हो सकता है। यह कार्ड निर्णय के रुप में योजनाओं का बाहर आना और सभी संबंध जैसे शक्ति, आदेश, अधिकार, अक्रामक बुद्धि और राजा के कानून के कार्यालय इत्यादि संबद्ध होता है।', ''),
(43, 'ऐस  ऑफ पेंटाकल्स ( Ace of Pentacles )', '/tarot/images/Rider_Waite/normal/pents01.jpg', 'धन की अपेक्षा करें|', ''),
(44, 'टू ऑफ पेंटाकल्स ( Two of Pentacles )', '/tarot/images/Rider_Waite/normal/pents02.jpg', 'अब एक से अधिक कार्य करने का समय है। एक आकस्मिक दोस्ताना माहौल एक अच्छा विचार है।', ''),
(45, 'थ्री ऑफ पेंटाकल्स ( Three of Pentacles )', '/tarot/images/Rider_Waite/normal/pents03.jpg', 'इस कार्ड में कुशल ट्रेडमैन और कलाकारों के जमाव को दर्शाया गया है|', ''),
(46, 'फोर  ऑफ पेंटाकल्स ( Four of Pentacles )', '/tarot/images/Rider_Waite/normal/pents04.jpg', 'भौतिक धन शक्ति ला सकता है ', ''),
(47, 'फाइव  ऑफ पेंटाकल्स ( Five of Pentacles )', '/tarot/images/Rider_Waite/normal/pents05.jpg', 'इसका मतलब बेरोजगारी और गरीबी हो सकता है। अलगाव।', ''),
(48, 'सिक्स  ऑफ पेंटाकल्स ( Six of Pentacles )', '/tarot/images/Rider_Waite/normal/pents06.jpg', 'उपहारों को काफी वितरित किया जाएगा।', ''),
(49, 'सेवन  ऑफ पेंटाकल्स ( Seven of Pentacles )', '/tarot/images/Rider_Waite/normal/pents07.jpg', ' रुका हुआ उद्यम के कारण असफलता संभव है। आप अपने वित्त को लेकर चिंतित महसूस करेंगे।', ''),
(50, 'ऐ ट  ऑफ पेंटाकल्स ( Eight of Pentacles )', '/tarot/images/Rider_Waite/normal/pents08.jpg', 'रोजगार के अवसर उपलब्ध हैं। यह एक कौशल  का उपयोग करने या सीखने का समय है। ', ''),
(51, 'नाइन  ऑफ पेंटाकल्स ( Nine of Pentacles )', '/tarot/images/Rider_Waite/normal/pents09.jpg', 'जीवन में साधारण चीजों जैसे बागवानी और परिवार का निजी आनंद महत्वपूर्ण हैं|', ''),
(52, 'टेन  ऑफ पेंटाकल्स ( Ten of Pentacles )', '/tarot/images/Rider_Waite/normal/pents10.jpg', 'पारिवारिक मामलों पर ध्यान दें। इसका मतलब घर खरीदना हो सकता है।', ''),
(53, 'पेज ऑफ पेंटाकल्स ( Page of Pentacles )', '/tarot/images/Rider_Waite/normal/pents11.jpg', 'यह एक विशेष शैक्षणिक व्यक्ति की तस्वीर है। आप पैसे के बारे में एक संदेश प्राप्त कर सकते हैं।', ''),
(54, 'नाइट ऑफ पेंटाकल्स ( Knight of Pentacles )', '/tarot/images/Rider_Waite/normal/pents12.jpg', ' कुछ वित्तीय मामले शुरू हो रहे हैं या बंद हो रहे हैं।', ''),
(55, 'क्वीन ऑफ पेंटाकल्स ( Queen of Pentacles )', '/tarot/images/Rider_Waite/normal/pents13.jpg', 'मुझे एक अमीर और चरित्रवान महिला दिखाई दे रही है।', ''),
(56, 'द  किंग ऑफ पेंटाकल्स ( King of Pentacles )', '/tarot/images/Rider_Waite/normal/pents14.jpg', 'आर्थिक रूप से प्रतिभाशाली व्यक्ति जो महान शक्ति रखता है। ', ''),
(57, 'द फूल ( The Fool )', '/tarot/images/Rider_Waite/normal/maj00.jpg', 'आपके सफल होने की बात की जाए तो आप भविष्य में अपनी उपलब्धियों को पाने के नए आयाम खोजने में सफल रह सकते हैं. अपने लक्ष्यों तक आप पहुंच ही जाएंगें लेकिन उसके लिए आपको नए-नए तरीके खोजने चाहिए कि कैसे आगे बढ़ा जाए. एक नई विचारधारा आप अपना सकते हैं. जब आपके समक्ष कोई नई उपलब्धि आएगी तब आप पुरानी वाली को उतार फेंकने के लिए तैयार रहते हैं. आप पुरानी उपलब्धियों को बहुत जल्द भुलाना चाहेगें. अगर भविष्य में आपके समक्ष किसी प्रकार की कोई नई उपलब्धि नहीं आती है तब आपको समझना चाहिए कि अब समय आ गया है कि आप अपने गुणों का उपयोग करें और स्वयं अपने लिए कोई नई राह चुनें और अपना रास्ता स्वयं बनाएँ. अपने बनाए तरीकों से आप आगे बढ़ने में सफल हो सकते हैं |', 'आप अपने लक्ष्य को पाने में कामयाब रहेगें. आप परेशानियों तथा बाधाओं को पीछे छोड़ते हुए आगे बढ़ सकते हैं. आप स्वयं को सुरक्षित महसूस करेगें |'),
(58, 'द मैजिशियन ( The Magician )', '/tarot/images/Rider_Waite/normal/maj01.jpg', 'यह कार्ड यदि आपके सामने आया है तब आपको थोड़ा सचेत हो जाना चाहिए क्योकि आपका भविष्य आपके सामने अनिश्चितता लिए खड़ा है. यह जोखिम भरा भी सिद्ध हो सकता है और अवसर प्रदान करने वाला भी सिद्ध हो सकता है. आपको दोनो ही स्थितियों के लिए तैयार रहना चाहिए. आपके सामने जो भी परिस्थिति या संभावनाएं बनेगी वह आपके पाले हुए मित्रो या आपके करीबी लोगो के द्वारा प्रभावित होगी. आप जीत हासिल करते हैं या सफलता का द्वार खोलते हैं तब आपके लिए यह नई शुरुआत हो सकती है. जो आपको चाहिए आप उसे करें और अपनी क्षमताओं को पहचानते हुए आगे बढ़े. अपनी प्रतिभा का उपयोग भरपूर मात्रा में करें |', 'यह कार्ड आपको इशारा कर रहा है है कि आप जो भी काम करना चाहते हैं उसके लिए आप अपनी इच्छाशक्ति का उपयोग करते हुए मजबूती से आगे कदम बढ़ाएं. आप जिस भी काम के लिए हाँ या ना जानना चाहते हैं उसका उत्तर “हाँ” है कि आपका काम होगा |'),
(59, 'द हाई प्रीस्टेस ( The High Priestess )', '/tarot/images/Rider_Waite/normal/maj02.jpg', 'यदि आप अभिप्रेरणाओ तथा अनुशासनबद्धता को जारी रखते हुए आगे बढ़ते रहेगें तब आपको जीवन में पुरुस्कारो की प्राप्ति हो सकती है. यह पुरुस्कार आपके असली व वास्तविक होगें. जो भावनाएं या काम आपकी राह में बाधाएँ पैदा करती है या आपको आगे बढ़ने से रोकती है उन्हें आप यदि स्वीकार नहीं करते हैं तब आपका भविष्य उज्जवल बन सकता है और आप जीवन में सफलता हासिल कर सकते हैं. अपनी अन्तरात्मा की आवाज का पालन करे और स्थाई रुप से संतोष पाने के लिए आगे बढ़े, आपको सफलता मिलेगी. आप अपनी क्षमताओ को पहचानते हुए भविष्य की ओर बढ़े. यदि आपको किसी बात में रहस्य लगे या कुछ छिपा हुआ महसूस हो तब आप उसे पहले जान ले तब आगे बढ़े |', 'आप अपने जीवन में अभी तक की बातों को खोजने के लिए स्थिर हैं और आपका प्रश्न अभी तक उलझा ही हुआ है. इसका अर्थ यह हुआ कि आप जो कुछ भी “हां” या “ना” में जानना चाहते हैं वह अभी भी आपके मन मस्तिष्क में पूरी तरह से स्पष्ट नही है |'),
(60, 'महारानी  ( The Empress )', '/tarot/images/Rider_Waite/normal/maj03.jpg', 'यह कार्ड बताता है कि भविष्य में आने वाला समय आपके भावनात्मक और शारीरिक घावों को भर सकता है जिससे आपको काफी राहत महसूस होगी. जिन कामो में बाधा आ रही है या जिस बात को आप बार-बार संवारने का प्रयास कर रहे हैं उसे रहने दे और अपना नया रास्ता बनाने का प्रयास करें. समय को नष्ट ना करते हुए आपको अपना पूरा समय नई योजनाओ पर लगाना चाहिए |', 'इस कार्ड से आपको समझना चाहिए कि आप आगे बढ़े अपनी पूरी क्षमता का उपयोग करें. इस कार्ड का आपके लिए यही मशविरा है कि आप अपनी क्षमताओ का भरपूर उपयोग करते हुए आगे बढ़े. आपके द्वारा पूछे गए प्रश्न का उत्तर आपके लिए “हाँ” है|'),
(61, 'सम्राट ( The Emperor )', '/tarot/images/Rider_Waite/normal/maj04.jpg', 'यदि आप किसी काम को करने की पहल करते है और उसका जिम्मा अपने कंधो पर लेते हैं तब आप अपने लक्ष्य तक पहुंच सकते हैं, लेकिन यह तभी संभव होगा जब आप सारी जिम्मेदारियों को अपने कंधों पर वहन करते हैं और कार्यवाही करने की ठानते है. वैसे तो जीवन में बहुत सी भूमिकाएँ व्यक्ति निभाता है लेकिन आपको इस समय उन बहुत सी भूमिकाओ में से किसी एक की जिम्मेदारी प्रदान की जाएगी. आप एक बात का ध्यान यह रखे कि जो आपके अनुगामी है उन्हें भूले नहीं|', 'आप अपने प्रयासो से आगे बढ़ रहे हैं और यह कार्ड आपकी दृढ़ता व आपकी तर्कशक्ति के बारे में बता रहा है कि आप बहुत आगे तक जाएंगे. इसलिए इस कार्ड के आने पर आपको अपना उत्तर “हाँ” समझना चाहिए.|'),
(62, 'द हीरोफैन्ट ( The Hierophant )', '/tarot/images/Rider_Waite/normal/maj05.jpg', 'इस कार्ड के आने का अर्थ है कि सफलता पाने के लिए आप प्रभावशाली और ठोस संगठन या संरचित सिद्धांतो की ओर देख रहे है. आपके कार्य स्वार्थ रहित होने चाहिए या उनके लिए थोड़ा सा ईनाम मिल सकता है. आप कोई भी काम करे तो स्वार्थीपन का परिचय ना देते हुए आगे बढ़े क्योकि आपको इसके लिए पुरस्कृत भी किया जा सकता है |', 'इस कार्ड का अर्थ ना तो सकारात्मक है और ना ही नकारात्मक ही है. यदि आप अपने प्रश्न का उत्तर हाँ या ना में जानना चाहते हैं तब इसका उत्तर “शायद” होगा अर्थात काम हो भी सकता है और नही भी. आप संशय की स्थिति में फंसे रह सकते हैं |'),
(63, 'द लवर्स ( The Lovers )', '/tarot/images/Rider_Waite/normal/maj06.jpg', 'आपके लिए यह समझना जरुरी है कि एक ही सिक्के के दो पहलू होते हैं जो एक-दूसरे से भिन्न होते हैं. एक बिना दूसरा नहीं हो सकता है हालांकि यह दोनो भिन्न हैं. आप सही निर्णय तभी ले सकते हैं जब आप दूसरों को अपने साथ ही स्वीकार करते हैं |', 'यह कार्ड आपको यह बताता है कि आप अपने जीवन में किसी दौराहे से होकर जा सकते हैं. यह कार्ड बहुत ही बली रुप में प्रेम, संबंधो और आपसी तालमेल का प्रतीक है. यदि पार्टनरशिप का प्रश्न है तब यह कार्ड सकारात्मक माना जाएगा. यह जीवन में किसी भी काम के लिए आगे बढ़ने के लिए हामी भर रहा है. आपके द्वारा पूछे गए “हाँ” या “ना” प्रश्न का उत्तर हाँ है |'),
(64, 'द चैरिट ( The Chariot )', '/tarot/images/Rider_Waite/normal/maj07.jpg', 'आप अभी इस समय जो भी संघर्ष कर रहे हैं उनसे डर कर ना भागें, आगे बढ़ते रहे, आपको सफलता अवश्य ही मिलेगी. अगर आप अपने संघर्षो को बीच में छोड़ते नहीं है तब आपको मान, सम्मान, पद, पहचान, प्रशंसा, ख्याति आदि मिलेगी. आप आत्म विश्वास के साथ लगे रहें |', 'इस कार्ड के माध्यम से आपका विश्वास, दिशा तथा जीत नजर आती है. यह कार्ड आपके प्रश्न का उत्तर हाँ में देता है. आप जिस भी कार्य के लिए इस समय मन में प्रश्न लेकर बैठे हैं उस कार्य के होने के संकेत सकारात्मक हैं |'),
(65, 'स्ट्रेंथ ( Strength )', '/tarot/images/Rider_Waite/normal/maj08.jpg', 'इस कार्ड का अर्थ है कि भविष्य में एक मजबूत व ऊर्जावान धारा का प्रवाह होने वाला है जो आपके जीवन में बहुत ही अहम बदलाव लाने का कारण बन सकता है. आप अपने जीवन को बेहतर बना सकते हैं यदि आप अपने खुद के मूल्यो पर विश्वास करें और उन्हें समझे. जीवन को सुधारने का अवसर बड़े ही अदभुत रुप से आपके सामने आ सकते हैं |', 'आपका जो भी प्रश्न है उसके लिए “हाँ” है कि आपका काम होने की संभावना बली है.|'),
(66, 'द हर्मिट ( The Hermit )', '/tarot/images/Rider_Waite/normal/maj09.jpg', 'जब आप स्वयं अपने सही रास्ते के बारे में जान लेगें कि आपको कहाँ जाना है तब उस समय आपको अपनी राह में किसी तरह की कोई रुकावट या बाधा नजर ही नहीं आएगी. आपके समक्ष तब कोई टिक ही नहीं पाएगा. जीवन आपको चुनौती दे सकता है लेकिन आप स्वयं को पहले से ही भविष्य के लिए मजबूत बना कर रखें. अपनी ताकत को समझें और चुनौतियों का डटकर मुकाबला करें|', 'आप इस समय जो भी प्रश्न हाँ या ना में जानना चाहते हैं उसका उत्तर इस समय “शायद” हो सकता है |'),
(67, 'द व्हील आफ फोरच्यून ( Wheel of Fortune )', '/tarot/images/Rider_Waite/normal/maj10.jpg', 'भविष्य आपके लिए कुछ ना कुछ ऎसा मौका लेकर आएगा जिससे आपकी राह व्यक्तिगत उन्नति व खुशी की ओर मुड़ जाएगी. आपके लिए जरुरी है कि आप अपनी सोच का दायरा बदलने के लिए तैयार हो जाएं, क्योकि आप चाहे या ना चाहे किन्तु आपके द्वारा किए गए कर्म सफलता लेकर आएंगें और यह लाभकारी परिवर्तन का प्रतिनिधित्व करते हैं |', 'यह कार्ड भाग्य को दर्शाता है, परिवर्तन दिखाता है और आपके जीवन की एक नई दिशा के बारे में बताता है. यह कार्ड उतार-चढ़ाव भी दिखाता है जबकि मोटे तौर पर देखा जाए तो कुल मिलाकर यह सकारात्मक कार्ड है जो समय को अच्छे और मजबूत भाग्य में बदलने का संकेत कर रहा है. आप जिस भी प्रश्न के लिए अपना है. उत्तर जानना चाहते हैं, उसका उत्तर “हाँ” है|'),
(68, 'जस्टिस ( Justice )', '/tarot/images/Rider_Waite/normal/maj11.jpg', 'जिन परिणामों के आप हकदार हैं उन्हें आप अवश्य ही प्राप्त करेगें. आपका नैतिक चरित्र न्यायोचित्त होगा इसलिए आप अपनी ईमानदारी, सत्यनिष्ठा व अखंडता को बनाए रखें और स्वयं को संतुलित रखने का प्रयास करते रहें|', 'आपका प्रश्न जिस भी क्षेत्र से संबंधित है उसका उचित जवाब या प्रत्यक्ष उत्तर इस कार्ड से नहीं मिलता है. स्थिति संदेहास्पद सी बनी रहेगी |'),
(69, 'द हैंग्ड मैन ( The Hanged Man )', '/tarot/images/Rider_Waite/normal/maj12.jpg', 'भविष्य की बहुत सी बातों से अभी आप अनभिज्ञ हैं, इसलिए आप कोई भी नया प्लान या लक्ष्य निर्धारित करने से पहले सावधानी अवश्य बरतें. सही समय पर सही दिशा में उठाया गया कदम आपको जीवन में आगे ले जाने में मदद करेगा|', 'यह कार्ड परिपाटियों अथवा कायदों को तोड़कर कुछ नया कायाकल्प होने की ओर इशारा करता है. आप समय को जाने दें या फिर आप इस समय परिवर्तन के काल से गुजर सकते हैं. हालांकि यह कार्ड कुछ नए नजरियों की ओर इशारा करता है इसलिए इसे ना तो सकारात्मक और ना ही नकारात्मक ही समझा जा सकता है. इस कार्ड का अर्थ - “शायद” है.|'),
(70, 'डैथ ( Death )', '/tarot/images/Rider_Waite/normal/maj13.jpg', 'यह कार्ड आपके सामने आता है तो इसका अर्थ है कि आप आगे बढ़े और आगे बढ़कर इस संसार के बारे में अपने एक नए दृष्टिकोण का विकास करें. आपका अतीत आपके पीछे है और आपका पूरा जीवन आपके आगे पड़ा है, इसलिए आप दिल की गहराईयों से अपनी जीवन यात्रा को जारी रखें|', 'अकसर इस कार्ड का संबंध हानि, असफलता या किसी तरह के विनाश से जोड़ा जाता है. कुल मिलाकर इस कार्ड को ज्यादा शुभ नहीं माना जाएगा यदि आपका प्रश्न हाँ या ना से जुड़ा है. आप जिस भी बात के लिए अपना उत्तर जानना चाहते हैं तब उसके लिए “ना” उत्तर होगा.|'),
(71, 'द टेम्पेरन्स ( Temperance )', '/tarot/images/Rider_Waite/normal/maj14.jpg', 'इच्छा को दूर करने और दूसरों के साथ सामंजस्य रखने की क्षमता।', ''),
(72, 'द डेविल ( The Devil )', '/tarot/images/Rider_Waite/normal/maj15.jpg', 'आपकी ताकत गलत सिरों का उपयोग हो रही है। आप कारल का पालन कर रहे हैं और कारण का उपयोग नहीं कर रहे हैं|', ''),
(73, 'टॉवर ( The Tower )', '/tarot/images/Rider_Waite/normal/maj16.jpg', 'पुराने तरीकों या मान्यताओं को चुनौती दी जानी है और उनका पुनर्मूल्यांकन किया जाना है। शोकपूर्ण घटना।', ''),
(74, 'द स्टार ( The Star )', '/tarot/images/Rider_Waite/normal/maj17.jpg', 'प्रेरित दान दूसरों को आशा देता है । दोनों तरह से प्यार बह रहा है।', ''),
(75, 'द मून ( The Moon )', '/tarot/images/Rider_Waite/normal/maj18.jpg', 'बेईमानी और अंधेरे बल। प्रियजनों के लिए बुरा भाग्य स्टोर में है |', ''),
(76, 'द  सन ( The Sun )', '/tarot/images/Rider_Waite/normal/maj19.jpg', 'आपको शादी और समाज जैसी साधारण चीजों में खुशी मिलेगी। ', ''),
(77, 'द जजमेंट ( Judgement )', '/tarot/images/Rider_Waite/normal/maj20.jpg', 'आपके द्वारा जीवन में किए गए प्रयास के बारे में अच्छा महसूस करें। आपने जीवन को अच्छी तरह से जीया है।', ''),
(78, 'द वर्ल्ड ( The World ) ', '/tarot/images/Rider_Waite/normal/maj21.jpg', 'सभी गतिविधियों में पूरी जीत के लिए मार्ग।', ''),
(79, 'ऐस ऑफ़ कप्स ( Ace of Cups )', '/tarot/images/Rider_Waite/reversed/cups01.jpg', 'प्यार, खुशी और फल की शुरुआत। आध्यात्मिकता भौतिक दुनिया को सहायता करती है।', 'प्यार, खुशी और फल की शुरुआत। आध्यात्मिकता भौतिक दुनिया को सहायता करती है।'),
(80, 'टू ऑफ कप (Two of Cups )', '/tarot/images/Rider_Waite/reversed/cups02.jpg', 'यह कार्ड बैलेंस दर्शाता है । आप विपरीत लिंग के साथ दोस्ती में प्रवेश कर सकते हैं।', 'यह कार्ड बैलेंस दर्शाता है । आप विपरीत लिंग के साथ दोस्ती में प्रवेश कर सकते हैं।'),
(81, 'थ्री ऑफ कप ( Three of Cups )', '/tarot/images/Rider_Waite/reversed/cups03.jpg', 'उपलब्धि और बहुतायत आपके रास्ते का नेतृत्व कर रहे हैं ।', 'उपलब्धि और बहुतायत आपके रास्ते का नेतृत्व कर रहे हैं ।'),
(82, 'फोर ऑफ कप ( Four of Cups )', '/tarot/images/Rider_Waite/reversed/cups04.jpg', '‘फोर ऑफ कप’ कभी कभी उदासीनता का एक संकेत देता है. आप वास्तव में किसी के बारे में बहुत परवाह नहीं है. आपको जीवन नीरस और साधारण लगता है. क्योंकि आप सफलता को पाने की रुचि खो देते हैं जो आपको ख़ुशी प्रदान करे. आप किसी भी दिशा में बहुत प्रयास करने की कोशिश नहीं करते और न ही इस बात से प्रेरित होते हैं. ऐसे समय में, ‘फोर ऑफ कप’ दिखा सकते हैं कि आप भावनात्मक रूप से ठहराव लिए हैं. आपको ध्यान केंद्रित करने की आवश्यकता है जिसके कारण आपका मार्ग स्पष्ट हो सके. अपने आप को अपने परिवेश में खोलें. जल्दी ही आप अपने रास्ते पर फिर से आ जाएंगे|', '‘फोर ऑफ कप’ कभी कभी उदासीनता का एक संकेत देता है. आप वास्तव में किसी के बारे में बहुत परवाह नहीं है. आपको जीवन नीरस और साधारण लगता है. क्योंकि आप सफलता को पाने की रुचि खो देते हैं जो आपको ख़ुशी प्रदान करे. आप किसी भी दिशा में बहुत प्रयास करने की कोशिश नहीं करते और न ही इस बात से प्रेरित होते हैं. ऐसे समय में, ‘फोर ऑफ कप’ दिखा सकते हैं कि आप भावनात्मक रूप से ठहराव लिए हैं. आपको ध्यान केंद्रित करने की आवश्यकता है जिसके कारण आपका मार्ग स्पष्ट हो सके. अपने आप को अपने परिवेश में खोलें. जल्दी ही आप अपने रास्ते पर फिर से आ जाएंगे|'),
(83, 'फाइव ऑफ कप ( Five of Cups )', '/tarot/images/Rider_Waite/reversed/cups05.jpg', 'फाइव ऑफ कप ( Five of Cups ), इसका अर्थ है असंतोष और नुकसान। ', 'फाइव ऑफ कप ( Five of Cups ), इसका अर्थ है असंतोष और नुकसान। '),
(84, 'सिक्स ऑफ कप ( Six of Cups )', '/tarot/images/Rider_Waite/reversed/cups06.jpg', 'अतीत से कुछ करने से आपको खुशी मिलेगी  | शायद बचपन के दोस्त से मिलेंगे।', 'अतीत से कुछ करने से आपको खुशी मिलेगी  | शायद बचपन के दोस्त से मिलेंगे।'),
(85, 'सेवन ऑफ कप ( Seven of Cups )', '/tarot/images/Rider_Waite/reversed/cups07.jpg', '“सेवन ऑफ कप” ध्यान और प्रतिबद्धता की कमी के साथ भी संबद्ध किया जा सकता है. यह बारीकी से परीक्षण करने के लिए विचारों को डालने से बचने के लिए संबंधित हो सकता है. इस कार्ड के साथ एक व्यक्ति खुद विचारों में रहता है और जीवन में इस तरह के एक दृष्टिकोण की वजह से अनेक चीजों को खो देता है|', 'इस कार्ड द्वारा सपनों, आशाओं और महत्वाकांक्षाओं का वर्णन किया गया है। आपकी इच्छाएं  केंद्रित नहीं हैं।'),
(86, 'एट ऑफ कप ( Eight of Cups )', '/tarot/images/Rider_Waite/reversed/cups08.jpg', ' ‘एट ऑफ कप’ के साथ इस बात का संकेत देता है कि उसने निराशाजनक स्थिति को छोड़ दिया है.वह आम तौर पर खुद को खोल देता है और अक्सर एक अज्ञात लंबी यात्रा आरंभ कर देता है. इस तरह का व्यक्ति जाने देने में विश्वास रखता है. जो उसने शुरु किया हो  वह उसे समाप्त करके फिर चलता है|', ' ‘एट ऑफ कप’ के साथ इस बात का संकेत देता है कि उसने निराशाजनक स्थिति को छोड़ दिया है.वह आम तौर पर खुद को खोल देता है और अक्सर एक अज्ञात लंबी यात्रा आरंभ कर देता है. इस तरह का व्यक्ति जाने देने में विश्वास रखता है. जो उसने शुरु किया हो  वह उसे समाप्त करके फिर चलता है|'),
(87, 'नाइन ऑफ कप ( Nine  of Cups )', '/tarot/images/Rider_Waite/reversed/cups09.jpg', 'आपकी इच्छा सच होने वाली है. यह एक अद्भुत संभावना है, लेकिन आप सुनिश्चित करें कि आप जानते हैं कि आप वास्तव में क्या चाहते हैं और ज़िम्मेदारी  को अपनी इच्छा के साथ  स्वीकार किया जाना चाहिए. और अच्छे भाग्य का आनंद लें. , ‘नाइन ऑफ कप’ के साथ व्यक्ति अपनी इच्छाओं को पाता है. इस तरह के एक व्यक्ति को हमेशा अपने लक्ष्यों को प्राप्त कर लेते हैं. , ‘नाइन ऑफ कप’ के साथ व्यक्ति के सपने हमेशा सच होते हैं |', '‘नाइन ऑफ कप’ के साथ व्यक्ति के सपने हमेशा सच होते हैं'),
(88, 'टेन ऑफ कप ( Ten of Cups )', '/tarot/images/Rider_Waite/reversed/cups10.jpg', '‘टेन ऑफ कप’ को पारिवारिक सहयोग और परिवार के अन्य सदस्यों के साथ संबंध में अच्छे रिश्ते को दर्शाता है. इसके साथ यह एक दूसरे को माफ करने की बात भी कहता है गलतियों को क्षमा करने की बात कहता है |', '‘टेन ऑफ कप’ को पारिवारिक सहयोग और परिवार के अन्य सदस्यों के साथ संबंध में अच्छे रिश्ते को दर्शाता है. इसके साथ यह एक दूसरे को माफ करने की बात भी कहता है गलतियों को क्षमा करने की बात कहता है |'),
(89, 'कप्स का पेज ( Page of Cups )', '/tarot/images/Rider_Waite/reversed/cups11.jpg', 'पेज ऑफ कप’ आपको बताता है कि आपको किसी के प्रति प्रेम प्रदर्शित करने के लिए आकर्षक विचारशील इशारा करना चाहिए. यह आपसे पूछता है के लिए सहानुभूति और समझ व्यक्त. यह भी कहता है कि तुम अपने आप को किसी को चोट पहुँचाने के लिए माफ कर दो और कोई ओर जो तुम्हें चोट पहुंचाता है तो तुम उसे क्षमा कर दो. ‘पेज ऑफ कप’ आपसे पूछता है तुम बाहर तक पहुँचो और किसी के दिल को छू लो. तुम आगे जाओ और एक टूटे हुए रिश्ते में सुधार करने का प्रयास करो. आप भी किसी दिन को रोशन करने और क्रोध के बजाय देखभाल के साथ जवाब दो. ‘पेज ऑफ कप’ तुम्हे न्यायाधीश या निंदा करने से मना करता है |', 'आप भी किसी दिन को रोशन करने और क्रोध के बजाय देखभाल के साथ जवाब दो. ‘पेज ऑफ कप’ तुम्हे न्यायाधीश या निंदा करने से मना करता है |'),
(90, 'नाइट ऑफ कप ( Knight of Cups )', '/tarot/images/Rider_Waite/reversed/cups12.jpg', 'यह कार्ड एक सुस्त लेकिन आकर्षक युवा के बारे में बताता है। भौतिक वस्तुओं से उसका सुख प्राप्त होता है।', 'वह आगे बढ़ते रहे | '),
(91, 'क्वीन ऑफ कप ( Queen of Cups )', '/tarot/images/Rider_Waite/reversed/cups13.jpg', ' यह महिला जब चाहें तब सामान प्राप्त कर सकती है  | वह सीधा और प्रतिबद्ध है, हालांकि वह आपकी मदद करने के लिए अपने रास्ते से बाहर नहीं जाएगी।', ' यह महिला जब चाहें तब सामान प्राप्त कर सकती है  | वह सीधा और प्रतिबद्ध है, हालांकि वह आपकी मदद करने के लिए अपने रास्ते से बाहर नहीं जाएगी।'),
(92, 'द किंग ऑफ कप ( King of Cups )', '/tarot/images/Rider_Waite/reversed/cups14.jpg', 'कप का राजा कोई दयालु है, कानून और व्यवसाय दोनों में कुशल है जो उसके व्यवहार के लिए बहुत जवाबदेह है। वह किसी धार्मिक या सांसारिक संगठन से जुड़ा हो सकता है।', 'कप का राजा कोई दयालु है, कानून और व्यवसाय दोनों में कुशल है जो उसके व्यवहार के लिए बहुत जवाबदेह है। वह किसी धार्मिक या सांसारिक संगठन से जुड़ा हो सकता है।'),
(93, 'ऐस ऑफ वैँड्स ( Ace of Wands )', '/tarot/images/Rider_Waite/reversed/wands01.jpg', 'यह कार्ड एक कार्य के प्रारंभ के साथ संबद्ध होता है। इस कार्ड की प्रकृति सामान्य तौर पर आसपास के कार्ड से पता चलती है। यह निर्माण, आविष्कार, सत्ता, पैसा, भाग्य, वंशानुक्रम और कभी कभी विभिन्न प्रकार की आपदाओं के साथ संबद्ध होता है। आप निष्कर्ष के द्वारा कह सकते हैं कि यह कार्ड कभी कभी अपने चारों ओर मौजूद कार्ड पर निर्भर होता है। अगर ऐसा नहीं भी है, तब भी परिणाम निश्चित रूप से  होगा। इस कार्ड का तात्पर्य है कि व्यक्ति जो कुछ भी  कार्य कर रहा है उसमे उसे सर्वश्रेष्ठ से अधिक देना होगा। उसे  साहसी कदम उठाना पड़ेगा।', ''),
(94, 'टू ऑफ वैन्डस ( Two of Wands )', '/tarot/images/Rider_Waite/reversed/wands02.jpg', 'ऐसे व्यक्ति किसी क्षेत्र में अग्रणी होते हैं और कुछ नयी रचना करते हैं। इस कार्ड के साथ व्यक्ति दूसरों को अपनी स्थिति पर ले आता है। ऐसे व्यक्ति को जो कुछ भी उनके मन में हो उससे संबधित बात करने के लिये तीव्र इच्छुक होते है।', ''),
(95, 'थ्री ऑफ वैंडस ( Three of Wands )', '/tarot/images/Rider_Waite/reversed/wands03.jpg', 'द थ्री ऑफ वैंडस के साथ व्यक्ति एक जिम्मेदार पद को ग्रहण करते हैं और एक प्रतिनिधि के रूप में सेवारत रहते हैं। एक उदाहरण के तौर पर ऐसे व्यक्ति के पीछे रैलियों का समूह होता है और हमेशा आगे की योजना बनाते है। इस कार्ड के साथ व्यक्ति मुख्य भूमिका में एक नेता के रूप में कार्यरत होते है।', ''),
(96, 'फोर आफ वैन्ड्स ( Four of Wands )', '/tarot/images/Rider_Waite/reversed/wands04.jpg', 'यह कार्ड निर्माण और स्थापना को दर्शाता है। यह कार्ड सामान्य तौर पर अप्रत्याशित शुभ भाग्य के साथ संबंधित है।', ''),
(97, 'फाइव आफ वैन्ड्स ( Five of Wands )', '/tarot/images/Rider_Waite/reversed/wands05.jpg', 'द फाइवआआफ वांड्स असीमित दर्द को दर्शाता है। कार्य की गतिविधि इतनी बढ़ जाती है कि लोगों द्वारा इस पर ध्यान दिया जाता है और जरूरी नहीं कि दूसरों के द्वारा इसको सराहना मिले। अब आपको अपने कार्य के लिये और अधिक महात्वाकांक्षी होना होगा, जिससे कार्य में सफलता मिले।', ''),
(98, 'सिक्स आफ वैन्डस ( Six  of Wands )', '/tarot/images/Rider_Waite/reversed/wands06.jpg', 'यह कार्ड आपके कार्यों के आकलन और विश्लेषण के साथ संबद्ध होता है। यह सफलता के क्रम में अधिक से अधिक ज्ञान और सुधार के साथ जुड़ा होता है।', ''),
(99, 'सेवन ऑफ वैन्ड्स ( Seven  of Wands )', '/tarot/images/Rider_Waite/reversed/wands07.jpg', 'यह कार्ड अपने कार्यो में नई खोज का सुझाव देता है। यह कार्ड कार्य के नए क्षेत्रों में परिवर्तन करने की खोज के साथ संबद्ध किया जाता है। यह कार्ड प्रतिनिधित्व करता है कि आगे जाने की तुलना में यह कार्य का मूल उद्देश्य था|', ''),
(100, 'ऐट आफ वैंड्स ( Eight  of Wands )', '/tarot/images/Rider_Waite/reversed/wands08.jpg', 'यह कार्ड संचारण, बिक्री, उन्नति, क्रय- विक्रय और कार्य को बनाने या विचार करने को दर्शाता है। यह विवाहित व्यक्ति के घरेलू विवादों का भी प्रतिनिधित्व करता है।', ''),
(101, 'नाइन आफ वैन्ड्स ( Nine  of Wands )', '/tarot/images/Rider_Waite/reversed/wands09.jpg', 'यह कार्ड किसी भी कार्य के बारे में चिंतन शक्ति को दर्शाता है। यह व्यक्ति के मन से सभी संदेह को दूर कर देता है।', ''),
(102, 'टेन आफ वैन्डस ( Ten of Wands )', '/tarot/images/Rider_Waite/reversed/wands10.jpg', 'बिना विचार किये या उत्तेजना के साथ आरंभ किया गया कार्य जो कि भीमकाय  बन जाय और नियंत्रण से बाहर हो जाए। अन्य लोग उस योजना का प्रतिरुपण या उपयोग करते हैं। आप या तो नियंत्रण में नहीं रख पाए या आपके अधिक नियंत्रण में हो और आप सब कुछ पुन: सब कुछ आरंभ करें।', ''),
(103, 'पेज आफ वैन्डस ( Page of Wands )', '/tarot/images/Rider_Waite/reversed/wands11.jpg', 'द पेज आफ  वैन्ड्स दर्शाता है कि परिवार का नवयुवक , जो कि नवयुवती की तलाश में है।', ''),
(104, 'नाइट आफ वैन्डस \n ( Knight of Wands )', '/tarot/images/Rider_Waite/reversed/wands12.jpg', 'यह कार्ड प्रस्थान, उड़ान, अनुपस्थिति, निवास परिवर्तन और अलगाव से संबद्ध होता है।', ''),
(105, 'क्यूइन आफ वैंडस ( Queen of Wands )', '/tarot/images/Rider_Waite/reversed/wands13.jpg', 'यह कार्ड अच्छी फसल को दर्शाता है, जिसके अनेक मायने हो सकते हैं। यह कार्ड प्रेम या धन के साथ भी संबद्ध होता है।', ''),
(106, 'किंग आफ वैन्डस ( King of Wands )', '/tarot/images/Rider_Waite/reversed/wands14.jpg', 'यह कार्ड असीम शक्ति को दर्शाता है। इसको अच्छे विवाह से भी संबद्ध किया जाता है। यह कार्ड ईमानदारी और अप्रत्याशित विरासत से भी संबंधित होता है।', ''),
(107, 'ऐस ऑफ़ स्वॉर्ड्स ( Ace of Swords )', '/tarot/images/Rider_Waite/reversed/swords01.jpg', 'यह एक महान विजय का संकेत देता है।', ''),
(108, 'टू ऑफ स्वॉर्ड्स ( Two of Swords )', '/tarot/images/Rider_Waite/reversed/swords02.jpg', 'यह कार्ड रिश्ते में तनाव या गतिरोध का द्योतक है। ', ''),
(109, 'थ्री ऑफ स्वॉर्ड्स ( Three of Swords )', '/tarot/images/Rider_Waite/reversed/swords03.jpg', 'यह कार्ड व्यक्ति की ऐसी परिस्थिति को दर्शाता है कि वह पुराने और नए के बीच फैसला करने में असमर्थ होता है। वह इस दुविधा में रहता है कि वह जीवन में नई चीजों के साथ रहे या अतीत उससे अधिक बेहतर था।', ''),
(110, 'फॉर ऑफ स्वोसर्ड (Four of Swords)', '/tarot/images/Rider_Waite/reversed/swords04.jpg', 'द फॉर ऑफ स्वार्ड  विश्राम की पुनराप्ति के स्वास्थ्य के समय की है, द र्थी ऑफ स्वार्ड में हानि या नुकसान के अनुभव को आत्मसात करता है। यह सावधानी, आश्रय, एकांत, निर्वासन से संबंधित है।', ''),
(111, 'फ़ाइव ऑफ स्वोर्ड ( Five of Swords )', '/tarot/images/Rider_Waite/reversed/swords05.jpg', 'यह कार्ड व्यक्ति के भाग्य पर हमले के साथ संबद्ध किया जाता है। यह अपमान, विनाश, अपयश, निरादर, और हानि के साथ संबंधित है।', ''),
(112, 'सिक्स ऑफ स्वोर्डस ( Six of Swords )', '/tarot/images/Rider_Waite/reversed/swords06.jpg', 'द सिक्स ऑफ स्वार्ड समय से बाहर निकलना, यात्रा, अध्ययन और सीखने  से संबंधित है। यह आपके ज्ञान और ज्ञान के संचय का सशक्त माध्यम है। यह आपको यात्रा करने के लिये और आंतरिक और बाहरी दुनिया की  खोज करने के लिये प्रेरित करता है।', ''),
(113, 'सेवन ऑफ स्वॉर्ड्स ( Seven of Swords )', '/tarot/images/Rider_Waite/reversed/swords07.jpg', 'यह कार्ड व्यक्ति के मन में संदेह बनाता है कि उसके प्रयास  और विचार महत्वपूर्ण है कि नही। यह कार्ड सुरक्षित रहने की क्षमता के बाद अच्छे जीवन का वादा करता है।', ''),
(114, 'ऐट ऑफ स्वोर्डस ( Eight of Swords )', '/tarot/images/Rider_Waite/reversed/swords08.jpg', 'ऐंट ऑफ स्वार्ड आपकी भावनात्मक और शारीरिक आवश्यकताओं के लिये निरंतर विचार और हानि के विश्लेषण को दर्शाता है। जैसी विचार और योजनायें होती है, उसी रुप में विचार और योजनायें मस्तिष्क में रहती है। आप अपने आप से अलग होकर सोचेंगे,  इसलिये आपका बाहरी अनुभव करने के लिए कोई संबंध नही होता है ।', ''),
(115, 'नाइन ऑफ स्वोर्डस ( Nine of Swords )', '/tarot/images/Rider_Waite/reversed/swords09.jpg', 'नाइन ऑफ स्वार्ड जो कि  द ऐंट ऑफ स्वार्ड से जारी उसकी अलग सोच के परिणामस्वरूप निराशा, उदासी, और हताशा को दर्शाता है। हम सब दुख और पीड़ा के लिए अभिशप्त होते हैं। प्रत्येक चीज समाप्त और क्षीण हो जाती है।', ''),
(116, 'टेन ऑफ स्वोर्डस ( Ten of Swords )', '/tarot/images/Rider_Waite/reversed/swords10.jpg', 'यह कार्ड सोच की प्रक्रिया के टूटने के साथ संबद्ध होता है। यह घनिष्टता से क्रोध और झुंझुलाहट के साथ संबंधित है। यह दूसरों द्वारा दी गई यातना और मानसिक दुख के साथ संबद्ध होता है।', ''),
(117, 'पेज ऑफ स्वोर्ड्स ( Page of Swords )', '/tarot/images/Rider_Waite/reversed/swords11.jpg', 'पेज ऑफ स्वार्ड को  ईर्ष्यालु जवान स्त्री माना जाता है जो सिर्फ खामोशी से क्या हो रहा है का अवलोकन करती है। इस कार्ड को अधिकार, सतर्कता, जासूसी, निरीक्षण और परीक्षा के साथ संबद्ध किया जा सकता है।', ''),
(118, 'नाईट ऑफ स्वोर्ड्स ( Knight of Swords )', '/tarot/images/Rider_Waite/reversed/swords12.jpg', 'इस कार्ड को आदर्शवादी और सकारात्मक विचारों से भरा माना जाता है। यह कौशल, बहादुरी, क्षमता, रक्षा, दुश्मनी, क्रोध, युद्ध, विनाश, विरोध, प्रतिरोध और पतन के साथ संबद्ध किया जाता है।', ''),
(119, 'क्वीन ऑफ स्वोर्ड्स ( Queen of Swords )', '/tarot/images/Rider_Waite/reversed/swords13.jpg', 'यह कार्ड एक स्त्री का है जिसने प्यार को खो दिया है या वह विधवा है। इसका संबंध स्त्री के दुख से, शर्मिंदगी, अनुपस्थिति, बाँझपन, शोक और अलगाव से संबंधित है।', ''),
(120, 'द किंग ऑफ स्वार्ड ( King of Swords )', '/tarot/images/Rider_Waite/reversed/swords14.jpg', 'द किंग ऑफ स्वार्ड गंभीर व्यवसायिक व्यक्ति है। यह एक कार्यालय में काम करता है और एक वकील हो सकता है। यह कार्ड निर्णय के रुप में योजनाओं का बाहर आना और सभी संबंध जैसे शक्ति, आदेश, अधिकार, अक्रामक बुद्धि और राजा के कानून के कार्यालय इत्यादि संबद्ध होता है।', ''),
(121, 'ऐस  ऑफ पेंटाकल्स ( Ace of Pentacles )', '/tarot/images/Rider_Waite/reversed/pents01.jpg', 'धन की अपेक्षा करें|', ''),
(122, 'टू ऑफ पेंटाकल्स ( Two of Pentacles )', '/tarot/images/Rider_Waite/reversed/pents02.jpg', 'अब एक से अधिक कार्य करने का समय है। एक आकस्मिक दोस्ताना माहौल एक अच्छा विचार है।', ''),
(123, 'थ्री ऑफ पेंटाकल्स ( Three of Pentacles )', '/tarot/images/Rider_Waite/reversed/pents03.jpg', 'इस कार्ड में कुशल ट्रेडमैन और कलाकारों के जमाव को दर्शाया गया है|', ''),
(124, 'फोर ऑफ पेंटाकल्स ( Four of Pentacles )', '/tarot/images/Rider_Waite/reversed/pents04.jpg', 'भौतिक धन शक्ति ला सकता है|', ''),
(125, 'फाइव ऑफ पेंटाकल्स ( Five of Pentacles )', '/tarot/images/Rider_Waite/reversed/pents05.jpg', 'इसका मतलब बेरोजगारी और गरीबी हो सकता है। अलगाव।', ''),
(126, 'सिक्स ऑफ पेंटाकल्स ( Six of Pentacles )', '/tarot/images/Rider_Waite/reversed/pents06.jpg', 'उपहारों को काफी वितरित किया जाएगा।', ''),
(127, 'सेवन ऑफ पेंटाकल्स ( Seven of Pentacles )', '/tarot/images/Rider_Waite/reversed/pents07.jpg', 'रुका हुआ उद्यम के कारण असफलता संभव है। आप अपने वित्त को लेकर चिंतित महसूस करेंगे।', ''),
(128, 'ऐट ऑफ पेंटाकल्स ( Eight of Pentacles )', '/tarot/images/Rider_Waite/reversed/pents08.jpg', 'रोजगार के अवसर उपलब्ध हैं। यह एक कौशल का उपयोग करने या सीखने का समय है।', ''),
(129, 'नाइन ऑफ पेंटाकल्स ( Nine of Pentacles ) ', '/tarot/images/Rider_Waite/reversed/pents09.jpg', 'जीवन में साधारण चीजों जैसे बागवानी और परिवार का निजी आनंद महत्वपूर्ण हैं|', ''),
(130, 'टेन ऑफ पेंटाकल्स ( Ten of Pentacles )', '/tarot/images/Rider_Waite/reversed/pents10.jpg', 'पारिवारिक मामलों पर ध्यान दें। इसका मतलब घर खरीदना हो सकता है।', ''),
(131, 'पेज ऑफ पेंटाकल्स ( Page of Pentacles )', '/tarot/images/Rider_Waite/reversed/pents11.jpg', 'यह एक विशेष शैक्षणिक व्यक्ति की तस्वीर है। आप पैसे के बारे में एक संदेश प्राप्त कर सकते हैं।', ''),
(132, 'नाइट ऑफ पेंटाकल्स ( Knight of Pentacles )', '/tarot/images/Rider_Waite/reversed/pents12.jpg', 'कुछ वित्तीय मामले शुरू हो रहे हैं या बंद हो रहे हैं।', ''),
(133, ' क्वीन ऑफ पेंटाकल्स ( Queen of Pentacles )', '/tarot/images/Rider_Waite/reversed/pents13.jpg', 'मुझे एक अमीर और चरित्रवान महिला दिखाई दे रही है।', ''),
(134, 'द किंग ऑफ पेंटाकल्स ( King of Pentacles )', '/tarot/images/Rider_Waite/reversed/pents14.jpg', 'आर्थिक रूप से प्रतिभाशाली व्यक्ति जो महान शक्ति रखता है।', ''),
(135, 'द फूल ( The Fool )', '/tarot/images/Rider_Waite/reversed/maj00.jpg', 'आपके सफल होने की बात की जाए तो आप भविष्य में अपनी उपलब्धियों को पाने के नए आयाम खोजने में सफल रह सकते हैं. अपने लक्ष्यों तक आप पहुंच ही जाएंगें लेकिन उसके लिए आपको नए-नए तरीके खोजने चाहिए कि कैसे आगे बढ़ा जाए. एक नई विचारधारा आप अपना सकते हैं. जब आपके समक्ष कोई नई उपलब्धि आएगी तब आप पुरानी वाली को उतार फेंकने के लिए तैयार रहते हैं. आप पुरानी उपलब्धियों को बहुत जल्द भुलाना चाहेगें. अगर भविष्य में आपके समक्ष किसी प्रकार की कोई नई उपलब्धि नहीं आती है तब आपको समझना चाहिए कि अब समय आ गया है कि आप अपने गुणों का उपयोग करें और स्वयं अपने लिए कोई नई राह चुनें और अपना रास्ता स्वयं बनाएँ. अपने बनाए तरीकों से आप आगे बढ़ने में सफल हो सकते हैं |', 'आप अपने लक्ष्य को पाने में कामयाब रहेगें. आप परेशानियों तथा बाधाओं को पीछे छोड़ते हुए आगे बढ़ सकते हैं. आप स्वयं को सुरक्षित महसूस करेगें |'),
(136, 'द मैजिशियन ( The Magician )', '/tarot/images/Rider_Waite/reversed/maj01.jpg', 'यह कार्ड यदि आपके सामने आया है तब आपको थोड़ा सचेत हो जाना चाहिए क्योकि आपका भविष्य आपके सामने अनिश्चितता लिए खड़ा है. यह जोखिम भरा भी सिद्ध हो सकता है और अवसर प्रदान करने वाला भी सिद्ध हो सकता है. आपको दोनो ही स्थितियों के लिए तैयार रहना चाहिए. आपके सामने जो भी परिस्थिति या संभावनाएं बनेगी वह आपके पाले हुए मित्रो या आपके करीबी लोगो के द्वारा प्रभावित होगी. आप जीत हासिल करते हैं या सफलता का द्वार खोलते हैं तब आपके लिए यह नई शुरुआत हो सकती है. जो आपको चाहिए आप उसे करें और अपनी क्षमताओं को पहचानते हुए आगे बढ़े. अपनी प्रतिभा का उपयोग भरपूर मात्रा में करें |', 'यह कार्ड आपको इशारा कर रहा है है कि आप जो भी काम करना चाहते हैं उसके लिए आप अपनी इच्छाशक्ति का उपयोग करते हुए मजबूती से आगे कदम बढ़ाएं. आप जिस भी काम के लिए हाँ या ना जानना चाहते हैं उसका उत्तर “हाँ” है कि आपका काम होगा |'),
(137, 'द हाई प्रीस्टेस ( The High Priestess )', '/tarot/images/Rider_Waite/reversed/maj02.jpg', 'यदि आप अभिप्रेरणाओ तथा अनुशासनबद्धता को जारी रखते हुए आगे बढ़ते रहेगें तब आपको जीवन में पुरुस्कारो की प्राप्ति हो सकती है. यह पुरुस्कार आपके असली व वास्तविक होगें. जो भावनाएं या काम आपकी राह में बाधाएँ पैदा करती है या आपको आगे बढ़ने से रोकती है उन्हें आप यदि स्वीकार नहीं करते हैं तब आपका भविष्य उज्जवल बन सकता है और आप जीवन में सफलता हासिल कर सकते हैं. अपनी अन्तरात्मा की आवाज का पालन करे और स्थाई रुप से संतोष पाने के लिए आगे बढ़े, आपको सफलता मिलेगी. आप अपनी क्षमताओ को पहचानते हुए भविष्य की ओर बढ़े. यदि आपको किसी बात में रहस्य लगे या कुछ छिपा हुआ महसूस हो तब आप उसे पहले जान ले तब आगे बढ़े |', 'आप अपने जीवन में अभी तक की बातों को खोजने के लिए स्थिर हैं और आपका प्रश्न अभी तक उलझा ही हुआ है. इसका अर्थ यह हुआ कि आप जो कुछ भी “हां” या “ना” में जानना चाहते हैं वह अभी भी आपके मन मस्तिष्क में पूरी तरह से स्पष्ट नही है |'),
(138, 'महारानी  ( The Empress )', '/tarot/images/Rider_Waite/reversed/maj03.jpg', 'यह कार्ड बताता है कि भविष्य में आने वाला समय आपके भावनात्मक और शारीरिक घावों को भर सकता है जिससे आपको काफी राहत महसूस होगी. जिन कामो में बाधा आ रही है या जिस बात को आप बार-बार संवारने का प्रयास कर रहे हैं उसे रहने दे और अपना नया रास्ता बनाने का प्रयास करें. समय को नष्ट ना करते हुए आपको अपना पूरा समय नई योजनाओ पर लगाना चाहिए |', 'इस कार्ड से आपको समझना चाहिए कि आप आगे बढ़े अपनी पूरी क्षमता का उपयोग करें. इस कार्ड का आपके लिए यही मशविरा है कि आप अपनी क्षमताओ का भरपूर उपयोग करते हुए आगे बढ़े. आपके द्वारा पूछे गए प्रश्न का उत्तर आपके लिए “हाँ” है|'),
(139, 'सम्राट ( The Emperor )', '/tarot/images/Rider_Waite/reversed/maj04.jpg', 'यदि आप किसी काम को करने की पहल करते है और उसका जिम्मा अपने कंधो पर लेते हैं तब आप अपने लक्ष्य तक पहुंच सकते हैं, लेकिन यह तभी संभव होगा जब आप सारी जिम्मेदारियों को अपने कंधों पर वहन करते हैं और कार्यवाही करने की ठानते है. वैसे तो जीवन में बहुत सी भूमिकाएँ व्यक्ति निभाता है लेकिन आपको इस समय उन बहुत सी भूमिकाओ में से किसी एक की जिम्मेदारी प्रदान की जाएगी. आप एक बात का ध्यान यह रखे कि जो आपके अनुगामी है उन्हें भूले नहीं|', 'आप अपने प्रयासो से आगे बढ़ रहे हैं और यह कार्ड आपकी दृढ़ता व आपकी तर्कशक्ति के बारे में बता रहा है कि आप बहुत आगे तक जाएंगे. इसलिए इस कार्ड के आने पर आपको अपना उत्तर “हाँ” समझना चाहिए.|'),
(140, 'द हीरोफैन्ट ( The Hierophant )', '/tarot/images/Rider_Waite/reversed/maj05.jpg', 'इस कार्ड के आने का अर्थ है कि सफलता पाने के लिए आप प्रभावशाली और ठोस संगठन या संरचित सिद्धांतो की ओर देख रहे है. आपके कार्य स्वार्थ रहित होने चाहिए या उनके लिए थोड़ा सा ईनाम मिल सकता है. आप कोई भी काम करे तो स्वार्थीपन का परिचय ना देते हुए आगे बढ़े क्योकि आपको इसके लिए पुरस्कृत भी किया जा सकता है |', 'इस कार्ड का अर्थ ना तो सकारात्मक है और ना ही नकारात्मक ही है. यदि आप अपने प्रश्न का उत्तर हाँ या ना में जानना चाहते हैं तब इसका उत्तर “शायद” होगा अर्थात काम हो भी सकता है और नही भी. आप संशय की स्थिति में फंसे रह सकते हैं |'),
(141, 'द लवर्स ( The Lovers )', '/tarot/images/Rider_Waite/reversed/maj06.jpg', 'आपके लिए यह समझना जरुरी है कि एक ही सिक्के के दो पहलू होते हैं जो एक-दूसरे से भिन्न होते हैं. एक बिना दूसरा नहीं हो सकता है हालांकि यह दोनो भिन्न हैं. आप सही निर्णय तभी ले सकते हैं जब आप दूसरों को अपने साथ ही स्वीकार करते हैं |', 'यह कार्ड आपको यह बताता है कि आप अपने जीवन में किसी दौराहे से होकर जा सकते हैं. यह कार्ड बहुत ही बली रुप में प्रेम, संबंधो और आपसी तालमेल का प्रतीक है. यदि पार्टनरशिप का प्रश्न है तब यह कार्ड सकारात्मक माना जाएगा. यह जीवन में किसी भी काम के लिए आगे बढ़ने के लिए हामी भर रहा है. आपके द्वारा पूछे गए “हाँ” या “ना” प्रश्न का उत्तर हाँ है |'),
(142, 'द चैरिट ( The Chariot )', '/tarot/images/Rider_Waite/reversed/maj07.jpg', 'आप अभी इस समय जो भी संघर्ष कर रहे हैं उनसे डर कर ना भागें, आगे बढ़ते रहे, आपको सफलता अवश्य ही मिलेगी. अगर आप अपने संघर्षो को बीच में छोड़ते नहीं है तब आपको मान, सम्मान, पद, पहचान, प्रशंसा, ख्याति आदि मिलेगी. आप आत्म विश्वास के साथ लगे रहें |', 'इस कार्ड के माध्यम से आपका विश्वास, दिशा तथा जीत नजर आती है. यह कार्ड आपके प्रश्न का उत्तर हाँ में देता है. आप जिस भी कार्य के लिए इस समय मन में प्रश्न लेकर बैठे हैं उस कार्य के होने के संकेत सकारात्मक हैं |'),
(143, 'स्ट्रेंथ ( Strength )', '/tarot/images/Rider_Waite/reversed/maj08.jpg', 'इस कार्ड का अर्थ है कि भविष्य में एक मजबूत व ऊर्जावान धारा का प्रवाह होने वाला है जो आपके जीवन में बहुत ही अहम बदलाव लाने का कारण बन सकता है. आप अपने जीवन को बेहतर बना सकते हैं यदि आप अपने खुद के मूल्यो पर विश्वास करें और उन्हें समझे. जीवन को सुधारने का अवसर बड़े ही अदभुत रुप से आपके सामने आ सकते हैं |', 'आपका जो भी प्रश्न है उसके लिए “हाँ” है कि आपका काम होने की संभावना बली है.|'),
(144, 'द हर्मिट ( The Hermit )', '/tarot/images/Rider_Waite/reversed/maj09.jpg', 'जब आप स्वयं अपने सही रास्ते के बारे में जान लेगें कि आपको कहाँ जाना है तब उस समय आपको अपनी राह में किसी तरह की कोई रुकावट या बाधा नजर ही नहीं आएगी. आपके समक्ष तब कोई टिक ही नहीं पाएगा. जीवन आपको चुनौती दे सकता है लेकिन आप स्वयं को पहले से ही भविष्य के लिए मजबूत बना कर रखें. अपनी ताकत को समझें और चुनौतियों का डटकर मुकाबला करें|', 'आप इस समय जो भी प्रश्न हाँ या ना में जानना चाहते हैं उसका उत्तर इस समय “शायद” हो सकता है |');
INSERT INTO `tarot` (`id`, `card`, `img`, `des`, `yn`) VALUES
(145, 'द व्हील आफ फोरच्यून ( Wheel of Fortune )', '/tarot/images/Rider_Waite/reversed/maj10.jpg', 'भविष्य आपके लिए कुछ ना कुछ ऎसा मौका लेकर आएगा जिससे आपकी राह व्यक्तिगत उन्नति व खुशी की ओर मुड़ जाएगी. आपके लिए जरुरी है कि आप अपनी सोच का दायरा बदलने के लिए तैयार हो जाएं, क्योकि आप चाहे या ना चाहे किन्तु आपके द्वारा किए गए कर्म सफलता लेकर आएंगें और यह लाभकारी परिवर्तन का प्रतिनिधित्व करते हैं |', 'यह कार्ड भाग्य को दर्शाता है, परिवर्तन दिखाता है और आपके जीवन की एक नई दिशा के बारे में बताता है. यह कार्ड उतार-चढ़ाव भी दिखाता है जबकि मोटे तौर पर देखा जाए तो कुल मिलाकर यह सकारात्मक कार्ड है जो समय को अच्छे और मजबूत भाग्य में बदलने का संकेत कर रहा है. आप जिस भी प्रश्न के लिए अपना है. उत्तर जानना चाहते हैं, उसका उत्तर “हाँ” है|'),
(146, 'जस्टिस ( Justice )', '/tarot/images/Rider_Waite/reversed/maj11.jpg', 'जिन परिणामों के आप हकदार हैं उन्हें आप अवश्य ही प्राप्त करेगें. आपका नैतिक चरित्र न्यायोचित्त होगा इसलिए आप अपनी ईमानदारी, सत्यनिष्ठा व अखंडता को बनाए रखें और स्वयं को संतुलित रखने का प्रयास करते रहें|', 'आपका प्रश्न जिस भी क्षेत्र से संबंधित है उसका उचित जवाब या प्रत्यक्ष उत्तर इस कार्ड से नहीं मिलता है. स्थिति संदेहास्पद सी बनी रहेगी |'),
(147, 'द हैंग्ड मैन ( The Hanged Man )', '/tarot/images/Rider_Waite/reversed/maj12.jpg', 'भविष्य की बहुत सी बातों से अभी आप अनभिज्ञ हैं, इसलिए आप कोई भी नया प्लान या लक्ष्य निर्धारित करने से पहले सावधानी अवश्य बरतें. सही समय पर सही दिशा में उठाया गया कदम आपको जीवन में आगे ले जाने में मदद करेगा|', 'यह कार्ड परिपाटियों अथवा कायदों को तोड़कर कुछ नया कायाकल्प होने की ओर इशारा करता है. आप समय को जाने दें या फिर आप इस समय परिवर्तन के काल से गुजर सकते हैं. हालांकि यह कार्ड कुछ नए नजरियों की ओर इशारा करता है इसलिए इसे ना तो सकारात्मक और ना ही नकारात्मक ही समझा जा सकता है. इस कार्ड का अर्थ - “शायद” है.|'),
(148, 'डैथ ( Death )', '/tarot/images/Rider_Waite/reversed/maj13.jpg', 'यह कार्ड आपके सामने आता है तो इसका अर्थ है कि आप आगे बढ़े और आगे बढ़कर इस संसार के बारे में अपने एक नए दृष्टिकोण का विकास करें. आपका अतीत आपके पीछे है और आपका पूरा जीवन आपके आगे पड़ा है, इसलिए आप दिल की गहराईयों से अपनी जीवन यात्रा को जारी रखें|', 'अकसर इस कार्ड का संबंध हानि, असफलता या किसी तरह के विनाश से जोड़ा जाता है. कुल मिलाकर इस कार्ड को ज्यादा शुभ नहीं माना जाएगा यदि आपका प्रश्न हाँ या ना से जुड़ा है. आप जिस भी बात के लिए अपना उत्तर जानना चाहते हैं तब उसके लिए “ना” उत्तर होगा.|'),
(149, 'द टेम्पेरन्स ( Temperance )', '/tarot/images/Rider_Waite/reversed/maj14.jpg', 'इच्छा को दूर करने और दूसरों के साथ सामंजस्य रखने की क्षमता।', ''),
(150, 'द डेविल ( The Devil ) ', '/tarot/images/Rider_Waite/reversed/maj15.jpg', 'आपकी ताकत गलत सिरों का उपयोग हो रही है। आप कारल का पालन कर रहे हैं और कारण का उपयोग नहीं कर रहे हैं|', ''),
(151, 'टॉवर ( The Tower )', '/tarot/images/Rider_Waite/reversed/maj16.jpg', 'पुराने तरीकों या मान्यताओं को चुनौती दी जानी है और उनका पुनर्मूल्यांकन किया जाना है। शोकपूर्ण घटना।', ''),
(152, 'द स्टार ( The Star ) ', '/tarot/images/Rider_Waite/reversed/maj17.jpg', 'प्रेरित दान दूसरों को आशा देता है । दोनों तरह से प्यार बह रहा है।', ''),
(153, 'द मून ( The Moon )', '/tarot/images/Rider_Waite/reversed/maj18.jpg', 'बेईमानी और अंधेरे बल। प्रियजनों के लिए बुरा भाग्य स्टोर में है |', ''),
(154, 'द सन ( The Sun ) ', '/tarot/images/Rider_Waite/reversed/maj19.jpg', 'आपको शादी और समाज जैसी साधारण चीजों में खुशी मिलेगी।', ''),
(155, 'द जजमेंट ( Judgement )', '/tarot/images/Rider_Waite/reversed/maj20.jpg', 'आपके द्वारा जीवन में किए गए प्रयास के बारे में अच्छा महसूस करें। आपने जीवन को अच्छी तरह से जीया है।', ''),
(156, 'द वर्ल्ड ( The World )', '/tarot/images/Rider_Waite/reversed/maj21.jpg', 'सभी गतिविधियों में पूरी जीत के लिए मार्ग।', '');
